import React, { Component } from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

export default class DataLog extends Component {
    constructor(props){
        super(props);

        this.onChangeMoT = this.onChangeMoT.bind(this);
        this.onChangeDist = this.onChangeDist.bind(this);
        this.onChangeDur = this.onChangeDur.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            mot: '',
            dist: 0,
            dur: 0,
            date: new Date()
        }
    }

    componentDidMount(){
        this.setState({
            modes: ['car', 'bus','walking'],
            mot: ''

        })
    }

    onChangeMoT(e){
        this.setState({
            mot: e.target.value
        });
    }

    onChangeDist(e){
        this.setState({
            dist: e.target.value
        });
    }

    onChangeDur(e){
        this.setState({
            dur: e.target.value
        });
    }

    onChangeDate(date){
        this.setState({
            date: date
        });
    }

    onSubmit(e){
        e.preventDefault();

        const submission = {
            mot: this.state.mot,
            dist: this.state.dist,
            dur: this.state.dur,
            date: this.state.date
        }

        console.log(submission);
        window.location = '/';
    }
    render(){
        return(
            <div>
                <h3>Log Data</h3>
                <form onSubmt={this.onSubmit}>
                    <div className="form-group">
                        <label>Mode of Transportation</label>
                        <select ref="userInput"
                            required
                            className="form-control"
                            value={this.state.mot}
                            onChange={this.onChangeMoT}>
                            {
                                this.state.modes.map(function(modes){
                                    return <option
                                    key={modes}
                                    value={modes}>{modes}
                                    </option>
                                })
                            }
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Date</label>
                        <div>
                            <DatePicker
                                selected={this.state.date}
                                onChange={this.onChangeDate}/>
                        </div>
                    </div>
                </form>
            </div>
        )
    }
}
